Page({
  data: {
    visible: false,
    visible1: false,
    visible2: false,
    visible3: false,
    visible4: false,
    visible5: false,
    visible6: false,
    visible7: false,
  },
  // 默认键盘
  onTap() {
    this.setData({ visible: !this.data.visible });
  },
  // 没有小数点
  onTap1() {
    this.setData({ visible1: !this.data.visible1 });
  },
  // 带确认按钮
