    ],
    actions2: [
      {
        text: '复制',
        key: 'copy',
        description: "请选择复制内容"
      },
      {
        text: '修改',
        key: 'modify',
        disabled: true
      },
      {
        text: '删除',
        key: 'delete',
        danger: true,
      }
    ],

    actions3: [
      {
        text: '备份',
        key: 'backup',
        description: '请选择要备份的内容',
        icon: 'https://gw.alipayobjects.com/mdn/rms_e7937b/afts/img/A*I0oNS4RCQzUAAAAAAAAAAAAAARQnAQ'
      },
      {
        text: '保存',
        key: 'save',
        icon: 'https://gw.alipayobjects.com/mdn/rms_e7937b/afts/img/A*BNczQ7hmp2gAAAAAAAAAAAAAARQnAQ'
      },
      {
        text: '同步',
        key: 'sync',
        icon: 'https://gw.alipayobjects.com/mdn/rms_e7937b/afts/img/A*eRaVSrV56dIAAAAAAAAAAAAAARQnAQ'
      }
    ]
  },

  handleOpenBasic(e) {
    const { index } = e.target.dataset;
    this.setData({
      [`visible${index}`]: true
    })
  },

  handleCloseBasic(e) {
    const { index } = e.target.dataset;
    this.setData({
      [`visible${index}`]: false
    })
  },

  handleAction(item, index, e) {
    if (item.key === 'delete') {
      my.confirm({
        content: '你确定要删除吗?'
      })
      return;
    }
    my.alert({
      content: `你点击了${item.key}`
    })
  }
})

sourceCode.pages/Form/index.js:
import { Form } from 'antd-mini/es/Form/form';
import cityList from './city';

Page({
  form: new Form(),
  data: {
    fruitList: ['苹果', '香蕉', '橘子', '西瓜'],
    cityList,
    radioGroupOptions: [
      { value: 'cat', label: '🐱' },
      { value: 'fox', label: '🦊' },
    ],
    checkboxGroupOptions: [
      { value: 'tomato', label: '🍅' },
      { value: 'potato', label: '🥔' },
      { value: 'eggplant', label: '🍆' },
    ],
    selectorOptions: [
      { value: 'basketball', text: '🏀' },
      { value: 'football', text: '⚽️' },
      { value: 'badminton', text: '🏸️' },
    ],
  },
  handleRef(ref) {
    this.form.addItem(ref);
  },
  reset() {
    this.form.reset();
  },
  async submit() {
    const values = await this.form.submit();
    my.alert({
      title: '提交',
      content: JSON.stringify(values, null, 2),
    });
    console.log(values);
  },
  onUpload(localFile) {
    return new Promise((resolve) => {
      console.log('上传的图片为：', localFile);
      setTimeout(() => {
        resolve(
          'https://gw.alipayobjects.com/mdn/rms_226d75/afts/img/A*5m0ZQYhxhjEAAAAAAAAAAAAAARQnAQ'
        );
      }, 2000);
    });
  },
});

