import { Form } from 'antd-mini/es/Form/form';


Page({
  data: {
    position: 'horizontal',
  },
  form: new Form(),
  handleRef(ref) {
    this.form.addItem(ref);
  },
  reset() {
    this.form.reset();
  },
  toggle() {
    this.setData({
      position: this.data.position === 'horizontal' ? 'vertical' : 'horizontal',
    });
  },
  async submit() {
    const values = await this.form.submit();
    my.alert({
      title: '提交',
      content: JSON.stringify(values),
    });
  }
});

sourceCode.pages/FormWatch/index.js:
import { Form } from 'antd-mini/es/Form/form';


Page({
  form: new Form(),
  data: {
    changedValuesText: '',
    allValuesText: '',
  },
  onReady() {
    this.form.onValuesChange((changedValues, allValues) => {
      let changedValuesText = '';
      let allValuesText = '';
      Object.keys(changedValues).forEach(name => {
        const value = changedValues[name];
        changedValuesText += `${name}: ${value};`;
      });

      Object.keys(allValues).forEach(name => {
        const value = allValues[name];
        allValuesText += `${name}: ${value};`;
      });
      this.setData({
        changedValuesText,
        allValuesText,
      });
    });
  },
  handleRef(ref) {
    this.form.addItem(ref);
  },
  reset() {
    this.form.reset();
    this.setData({
      changedValuesText: '',
      allValuesText: '',
    });
  },
  async submit() {
    const values = await this.form.submit();
    my.alert({
      title: '提交',
      content: JSON.stringify(values),
    });
  }
});

sourceCode.pages/FormDynamic/index.js:
import { Form } from 'antd-mini/es/Form/form';


let account = 1;
Page({
  data: {
    list: [{
      id: `Account${account++}`,
    }, {
      id: `Account${account++}`,
    }],
  },
  form: new Form(),
  handleRef(ref) {
    this.form.addItem(ref);
  },
  reset() {
    this.form.reset();
  },
  add() {
    this.setData({
      list: [
        ... this.data.list,
        {
          id: `Account${account++}`,
        },
      ],
    });
  },
  minus(e) {
    const { index } = e.currentTarget.dataset;
    const list = [...this.data.list];
    list.splice(index, 1);
    this.setData({
      list,
    });
  },
  async submit() {
    const values = await this.form.submit();
    console.log(values);
  }
});

