import { Form } from 'antd-mini/es/Form/form';


Component({
  onInit() {
    this.form = new Form();
    this.form.setInitialValues(this.props.config.initialValues);
    this.form.reset();
  },

  methods: {
    handleRef(ref) {
      this.form.addItem(ref);
    },
    async submit() {
      const values = await this.form.submit();
