sourceCode.pages/TabsElevator/index.js:
function getBoundingClientRect(selector) {
  return new Promise(resolve => {
    my.createSelectorQuery()
      .select(selector)
      .boundingClientRect()
      .exec((ret) => {
        if (ret && ret[0]) {
          resolve(ret[0]);
        }
      });
  });
}



