  handleChange(value, items, e) {
    console.log(value, items, e);
  },
  onTapItem(e) {
    console.log(e);
    const { index } = e.target.dataset;
    const { currentOpen } = this.data;
    let value = index;
    if (currentOpen === index) {
      value = -1;
    }
    this.setData({ currentOpen: value });
  },
});

